mapper.update:
update payment set payment_class= payment_class+3
where truck_code=#{truck_code}
and payment_telephone=#{payment_telephone}
and payment_code like #{payment_code}||'%'

mapper.select:

select a.*, b.menu_name
from payment a left outer join menu b on (a.menu_code = b.menu_code)
where to_date(a.payment_date, 'YY-MM-DD') >= TRUNC(TO_DATE(sysdate, 'YY-MM-DD'), 'IW') and to_date(a.payment_date, 'YY-MM-DD') <= TRUNC(TO_DATE(sysdate, 'YY-MM-DD'), 'IW')+6 and a.truck_code = #{truck_code} and (a.payment_class = 3 or a.payment_class = 4 or a.payment_class = 5)
order by a.payment_date asc;
